mapper.select:
select item_id as id, item_option as options, item_original_price as originalPrice, item_sale_price as salePrice, item_createdate as createdate, product_name as "product.name", product_show as "product.show", product_createdate as "product.createDate", company_id as "product.id", small_category_name as "product.smallCategory.name" from( select row_number() over(order by item_id asc, product_name asc) rn, I.item_id, I.item_option, I.item_original_price, I.item_sale_price, I.item_createdate, P.product_name, P.product_show, P.product_createdate, P.company_id, SC.small_category_name from items I, products P, small_categories SC where P.small_category_id = SC.small_category_id and P.product_id = I.product_id and P.company_id = ${companyId}
and item_option like '%' || #{criteria.keyword} || '%'
and small_category_name like '%' || #{criteria.keyword} || '%'
and company_id like '%' || #{criteria.keyword} || '%'
and product_show like '%' || #{criteria.keyword} || '%'
and product_createdate > #{criteria.beginday}
and product_createdate < #{criteria.endday} + 1
) where rn >= #{criteria.beginIndex} and rn <= #{criteria.endIndex}
